sys_script_include.script:
var HRM = Class.create();
HRM.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    performAction: function(ticket_number, action) {
        var gr = new GlideRecord('x_1527610_hrm_hrm_tickets');
        gr.addQuery('number', ticket_number);
        gr.query();

        if (gr.next()) {
            if (action == 'accept') {
                gr.state = 3; 
                gr.closed_by = gs.getUserID();
                gr.closed_date = new GlideDateTime(); 
                gr.update();
				return 'Ticket accepted successfully';
            } else if (action == 'reject') {
                gr.state = 11; 
                if (gr.reopen_count == 123456) {
                    gr.reopen_count = 1; 
                } else {
                    gr.reopen_count += 1; 
                }
                gr.reopened_by = gs.getUserID(); 
                gr.reopened_date = new GlideDateTime(); 
                gr.update(); 
				return 'Ticket rejected successfully';
            }
        }
    },
    type: 'HRM'
});
