sys_ws_operation.operation_script:
(function process (request, response) {

    var ticket_number = request.queryParams.ticket_number;
    var action = request.queryParams.action;
    var gr = new GlideRecord('x_1527610_hrm_hrm_tickets');

    gr.addQuery('number', ticket_number);
    gr.query();

    if(gr.next()){
        var acceptPageUrl = 'https://dev214635.service-now.com/x_1527610_hrm_TicketAcceptPage.do';
		var rejectPageUrl = 'https://dev214635.service-now.com/x_1527610_hrm_TicketRejectPage.do';
        if (action == 'accept') {
            gr.state = 3; 
            gr.closed_by = gs.getUserID(); 
            gr.closed_date = new GlideDateTime(); 
            gr.update(); 
        } else if (action == 'reject') {
            gr.state = 11;
            if(gr.reopen_count == 123456){
                gr.reopen_count = 1;
            } else {
                gr.reopen_count = gr.reopen_count + 1;
            }
            gr.reopened_by = gs.getUserID(); 
            gr.reopened_date = new GlideDateTime(); 
            gr.update();
        }
		if (action == 'accept') {
			response.setStatus(302); 
			response.setHeader('Location', acceptPageUrl);
		}
		else{
			response.setStatus(302); 
			response.setHeader('Location', rejectPageUrl);
		}
    }
})(request, response);
